import { useState  } from 'react';


function useFetch(url) {
    const [data , SetData] = useState(null);

    useEffect(() => {
      fetch(url)
          .then(res => res.json())
          .then(json => SetData(json))
    } , [url]);

    

  return (
    data
  );
}

export default useFetch
