import React from 'react'
import { useEffect } from 'react';
import { useState } from 'react';
import { useParams } from 'react-router-dom'

function Details() {
    const { id } = useParams();
    const [tarif,SetTarif] = useState(null);
    const url = `http://localhost:3000/tarifler/${id}`

    
    useEffect(() => {

        fetch(url)
        .then(res => res.json())
        .then(data => SetTarif(data))
    },[url] );

  return (
    <div className='row mt-4'>
        {isloading && <div className='alert alert-warnig'>Yükleniyor...</div>}
        {
            tarif && (
                <>
                    <div className="col-4 ">
                        <img src={"../img/" + tarif.resim} className="img-fluid rounded" alt={tarif.baslik}/>
                    </div>
                    <div className="col-8">
                        <h5>{tarif.baslik}</h5>
                        <p>{tarif.aciklama}</p>
                        <p>Malzemeler:</p>
                        <ul>
                            {
                                tarif.malzemeler.map((malzeme,index) => (
                                    <li key={index}>
                                        {malzeme}
                                    </li>
                                ))
                            }
                        </ul>
                    </div>
                    <div className="col-12">
                        <hr />
                        <p>{tarif.hazirlanisi}</p>
                        <a href={tarif.url} className="btn btn-outline-primary">İncele</a>
                    </div>
                </>
            )
        }
    </div>
  )
}

export default Details
