import React from 'react'
import { useState , useEffect } from 'react';

function Home() {
  const [tarifler , Settarifler] = useState(null);

  useEffect(() => {
    fetch("http://localhost:3000/tarifler")
        .then(res => res.json())
        .then(data => Settarifler(data))
  } , []);
    
  return (
    <div className="row mt-3 ">
        {
            tarifler && tarifler.map(tarif =>(
                <div class="card col-3 mb-3 m-2">
                    <img src={"img/" + tarif.resim} class="card-img-top" alt={tarif.baslik}/>
                    <div class="card-body">
                        <h5 class="card-title">{tarif.baslik}</h5>
                        <p class="card-text">{tarif.aciklama}</p>
                        <a href="#" class="btn btn-outline-primary">Detaylar</a>
                    </div>
                </div>
            ))
        }
    </div>
  )
}

export default Home
