import { useState , useEffect } from 'react';


function useFetch({url}) {
    const [data , SetData] = useState({
    "id": "1",
    "baslik": "Halka Kebabı",
    "aciklama": "8-10 kişilik, 15dk Hazırlık, 30dk Pişirme",
    "malzemeler": [
      "500 gram kıyma",
      "1 çay kaşığı tuz",
      "Yarım çay kaşığı karabiber",
      "3 adet patlıcan",
      "3 adet patates"
    ],
    "hazirlanisi": "Patlıcanların kabuklarını alacalı olarak soyalım ve yarım parmak genişliğinde dilimleyelim.Ardından su dolu kabın içerisine tuz ekleyip karıştıralım ve patlıcanları acısının çıkması için suya alarak 20 dakika bekletelim. Kabuklarını soyduğumuz soğanları da daire şeklinde dilimleyelim.",
    "resim": "halka-kebabi.jpg",
    "url": "https://www.nefisyemektarifleri.com/video/halka-kebabi/"
  });

    useEffect(() => {
      fetch(url)
          .then(res => res.json())
          .then(json => SetData(json))
    } , [url]);

  return (
    data
  )
}

export default useFetch
