import { useState , useEffect } from 'react';


const useFetch = (url) => {
  const [data,setData] = useState(null);

  useEffect(() => {
    fetch(url)
      .then(res => res.json())
      .then(json => setData(json))
   },[url])


  return (
    json
  );

}

export default useFetch
