import React from 'react'
import { Link, useParams } from 'react-router-dom'
import useFetch from '../../hooks/useFetch';

function Details() {
    const { id } = useParams();
    const url = `http://localhost:3000/tarifler/${id}`;

    const {data: tarif , isloading } = useFetch(url);

  return (
    <div className='row mt-4'>
        {isloading && <div className='alert alert-warning'>Yükleniyor...</div>}
        {
            tarif && (
                <>
                    <div>
                        <Link class="bi bi-arrow-left">
                            <h5>{tarif.baslik}</h5>
                        </Link> 
                    </div>
                    <hr />
                    <div className="col-4 ">
                        <img src={"../img/" + tarif.resim} className="img-fluid rounded" alt={tarif.baslik}/>
                    </div>
                    <div className="col-8">
                        <p>{tarif.aciklama}</p>
                        <p>Malzemeler:</p>
                        <ul>
                            {
                                tarif.malzemeler.map((malzeme,index) => (
                                    <li key={index}>
                                        {malzeme}
                                    </li>
                                ))
                            }
                        </ul>
                    </div>
                    <div className="col-12">
                        <hr />
                        <p>{tarif.hazirlanisi}</p>
                        <a href={tarif.url} className="btn btn-outline-primary">İncele</a>
                    </div>
                </>
            )
        }
    </div>
  )
}

export default Details
