import { useState , useEffect } from 'react';


const useFetch = (url) => {
  const [data,setData] = useState(null);
  const [isloading,setIsloading] = useState(false );

  useEffect(() => {
    setIsloading(true);
    fetch(url)
      .then(res => res.json())
      .then(data => setData(data))
    setIsloading(false);  
   },[url])


  return {
    data
  }

}

export default useFetch
