import React from 'react'
import { useEffect } from 'react';
import { useState } from 'react';
import { useParams } from 'react-router-dom'

function Details() {
    const { id } = useParams();
    const [tarif,SetTarif] = useState(null);
    const url = `http://localhost:3000/tarifler/${id}`

    useEffect(() => {

        fetch(url)
        .then(res => res.json())
        .then(data => SetTarif(data))
    },[url] );

  return (
    <div className='row'>
        {
            tarif && (
                <>
                    <div className="col-4">
                        <img src={"../img/" + tarif.resim} class="card-img-top" alt={tarif.baslik}/>
                    </div>
                    <div className="col-5">
                        <h2>{tarif.baslik}</h2>
                        <h3>malzemeler</h3>
                    </div>
                </>
            )
        }
    </div>
  )
}

export default Details
